import React, { useEffect, useState} from "react";
import { Navbar, Services } from "./components";
import Home from "./pages/Home";
import { useDispatch } from "react-redux";
import { Routes, BrowserRouter, Route } from "react-router-dom";
import { setCurrentScrollingLevel } from "./Redux/reducers/currentScrollingLevel";
import { khawarizmi, khalidibnyazid, jabirbnhayan, ibnsina, almajriti, alkindi, abbasbnfarnas } from './assets/stickers/' 
import { gsap } from 'gsap'

function App() {
  const dispatch = useDispatch();
  const [addSticker, setAddSticker] = useState(false)

  const scientists = [
    khawarizmi, khalidibnyazid, jabirbnhayan, ibnsina, almajriti, alkindi, abbasbnfarnas
  ]

  function throttle(func, delay) {
    let timeoutId;
    let lastExecTime = 0;
    return function (...args) {
      const currentTime = new Date().getTime();
      const timeSinceLastExec = currentTime - lastExecTime;
      if (!timeoutId && timeSinceLastExec >= delay) {
        func.apply(this, args);
        lastExecTime = currentTime;
      } else if (!timeoutId) {
        timeoutId = setTimeout(() => {
          timeoutId = null;
          lastExecTime = new Date().getTime();
          func.apply(this, args);
        }, delay - timeSinceLastExec);
      }
    };
  }
  useEffect(() => {
    window.addEventListener(
      "scroll",
      throttle(() => {
        dispatch(setCurrentScrollingLevel(window.pageYOffset));
      }, 100)
    );
  }, [dispatch]);


window.addEventListener('click', (e) => {
    setAddSticker(true)
    const sticker = document.createElement('div')
    const img = document.createElement('img')

    sticker.style.width = 100 + 'px'
    sticker.style.height = 100 + 'px'
    sticker.style.position = 'absolute'
    sticker.style.left = e.pageX + 'px'
    sticker.style.top = e.pageY + 'px'
    sticker.style.transform = `rotate(${Math.floor(Math.random() * 90)}deg)` 

    gsap.to(sticker, { transform: `rotate(${Math.floor(Math.random() * 90)}deg)`, duration: 0.3} )
    gsap.to(sticker, { transform: `rotate(${Math.floor(Math.random() * 90)}deg)`, duration: 0.3} )

    img.src = scientists[Math.floor(Math.random() * scientists.length)]
    img.setAttribute('id', 'sticker')

    addSticker === true ? document.body.appendChild(sticker) : console.log('no')

    sticker.appendChild(img)

    setTimeout(() => {
      sticker.style.display = 'none'
    }, 1000)
  })

  return (
    <div className="thecontainer max-w-[1440px] mx-auto relative">
      <BrowserRouter>
        <Navbar /> 
        <div 
          onClick={() => setAddSticker(false)}
          className="bg-red-500 w-full h-60">
        </div>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/services" element={<Services />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
