import React, { useEffect} from "react";
import { Navbar, Services, Service, Admin} from "./components";
import Home from "./pages/Home";
import { useDispatch } from "react-redux";
import { Routes, BrowserRouter, Route } from "react-router-dom";
import { setCurrentScrollingLevel } from "./Redux/reducers/currentScrollingLevel";

function App() {
  const dispatch = useDispatch();

  function throttle(func, delay) {
    let timeoutId;
    let lastExecTime = 0;
    return function (...args) {
      const currentTime = new Date().getTime();
      const timeSinceLastExec = currentTime - lastExecTime;
      if (!timeoutId && timeSinceLastExec >= delay) {
        func.apply(this, args);
        lastExecTime = currentTime;
      } else if (!timeoutId) {
        timeoutId = setTimeout(() => {
          timeoutId = null;
          lastExecTime = new Date().getTime();
          func.apply(this, args);
        }, delay - timeSinceLastExec);
      }
    };
  }
  useEffect(() => {
    window.addEventListener(
      "scroll",
      throttle(() => {
        dispatch(setCurrentScrollingLevel(window.pageYOffset));
      }, 100)
    );
  }, [dispatch]);

  useEffect(() => {
    const bgElements = document.querySelectorAll(".thecontainer");
    bgElements.forEach(function(element) {
      const randomX = Math.floor(Math.random() * 101);
      const randomY = Math.floor(Math.random() * 101);
      element.style.backgroundPosition = "calc(" + randomX + "%) calc(" + randomY + "%)";
    });
  }, []);


  return (
    <div className="thecontainer relative max-w-[1536px] mx-auto">
      <BrowserRouter>
        <Navbar /> 
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/services" element={<Services />} />
          <Route path="/:serviceId/:itemId" element={<Service />}/>
          <Route path="/admin" element={<Admin />}/>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
