import { useEffect, useState } from 'react'

export const TitleAndInput = ({ title, setServiceTitle, setServiceNumber}) => {
  return (
  <div className="flex flex-col gap-1">
    <h3 className="text-sm font-bold">{title}</h3>
    <input  type="text" name={title} className="w-full md:w-80 h-10 rounded-md focus:outline-none border-2 border-black px-2 bg-transparent text-black"/>
  </div>
  )
}

export const CreateService = () => {

const [serviceTitle, setServiceTitle] = useState("")
const [serviceNumber, setServiceNumber] = useState(-1)
const [serviceItems, setServiceItems] = useState([])


    const createService = async (title, number, items) => {
      const respense = await fetch('http://localhost:4000/api/services/', {
        method: "POST",
        body: JSON.stringify([
        {
        "title": title,
        "number": number,
        "items": items
      }
      ])
    })
  }

  return (
    <form onSubmit={(e) => {
      e.preventDefault()
      createService(serviceTitle, serviceNumber, serviceItems)
    }} method="POST" className="h-screen w-full flex md:items-center justify-center p-10 flex-col pt-20 gap-10">


      <div className="flex flex-col gap-1 items-center">
    <h3 className="text-sm font-bold flex gap-4">Service body </h3>
        <textarea onChange={(e) => {
          setServiceItems(e.target.value)
        }} className="w-full md:w-80 h-50 rounded-md focus:outline-none border-2 border-black p-2 bg-transparent text-black"></textarea>
      </div>

        <button type="submit" className="bg-black text-white rounded-md cursor-pointer py-3 px-5 hover:border-white border-2 border-transparent">Create Service</button>
    </form>
  )
}
