import { BsLightning } from 'react-icons/bs'

export const Services = () => {
  const services = [
    {
      id: 1,
      servicesTitle: 'Landing Pages',
      services: [
        {id: 1, serviceName: 'Landing Page One'},
        {id: 2, serviceName: 'Landing Page two'},
        {id: 3, serviceName: 'Landing Page three'},
      ]
    },
    {
      id: 2,
      servicesTitle: 'Portfolio Websites',
      services: [
        {id: 1, serviceName: 'Portfolio One'},
        {id: 2, serviceName: 'Portfolio two'},
        {id: 3, serviceName: 'portfolio three'},
      ]
    },
  ]
  return (
  <div className="flex flex-col justify-center items-center p-10 gap-20 pt-36">
      {
        services.map((service) => (
      <div key={service.id} className="flex flex-col gap-8 text-sm w-full">
        <h1 className="font-bold text-lg ">{service.servicesTitle}</h1>
        <div className="flex flex-wrap gap-10 justify-center items-center">
          {
            service.services.map((s) => (
            <div key={s.id} className={`w-60 h-96 flex p-4 border-4 hover:border-[#8eddff] border-transparent rounded-lg ${s.id === 2 ? 'bg-[#131313] text-white' : 'bg-white'}`}>

            <div className="flex justify-between w-full">
              <h3 className="text-xs font-bold">{s.serviceName}</h3>
              <div className="flex justify-center items-center bg-[#131313]/30 text-[#131313] w-12 h-12 rounded-full">
              <div className="flex justify-center items-center bg-[#131313]/40 text-white text-lg w-9 h-9 rounded-full">
               <BsLightning /> 
              </div>
              </div>
            </div>

            </div>
            ))
          } 
        </div>
      </div>
        ))
      }

  </div>
  )
}
